// import dataProducts from 'http://localhost:4000/getData';
import axios from 'axios';
import HeaderNavbar from './components/HeaderNavbar';
import HeaderSortBar from './components/HeaderSortBar';
import HeaderWithSearch from './components/HeaderWithSearch';
import React, { useEffect, useState } from 'react';
import Category from './components/Category';
import HomeFilter from './components/HomeFilter';
import MobileCategory from './components/MobileCategory';
// import Pagination from './components/Pagination';
import ProductCard from './components/ProductCard';
import HomeScreen from './components/HomeScreen';
import ProductScreen from './components/ProductScreen';

import ReactDOM from 'react-dom';
import ReactPaginate from 'react-paginate';
import { BrowserRouter, Routes , Route, Link } from 'react-router-dom';

const LIMIT = 5;

function App () {
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(false);
  const [offset, setOffset] = useState(0);
  const [pageCount, setPageCount] = useState(0);



  useEffect(() => {
    const getProductData = async () => {

      setLoading(true)
      try {
        const res = await axios.get('http://localhost:3600/products', {
          params:{
            limit: LIMIT,
            offset,
          }
        })
        setPosts(res.data.posts);
        setLoading(false);
        setPageCount(Math.ceil(res.data.total / LIMIT))
      } catch (error) {
        console.log(error);
      }
    }

    getProductData()
  }, [offset])

  if (loading && posts.length === 0) {
    return <h2>Loading...</h2>
  }


  const changePage = (event) => {
    const newOffset = (event.selected * LIMIT);
    setOffset(newOffset);
    console.log(event.selected)
  }
  //   const changePage = ({ selected }) => {
  //   setOffset(selected);
  // };
  // const indexOfLastPost = currentPage * postsPerPage;
  // const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost)
  // const howManyPages = Math.ceil(posts.length/postsPerPage)

  return(
    <BrowserRouter>
    <div className="app">
      <header className="header">
          <div className="grid wide">
            <HeaderNavbar/>
            <HeaderWithSearch/>
          </div>
          <HeaderSortBar/>
      </header>
      <div className="app__container">   
          <div className="grid wide">
            <div className="row sm-gutter app__content">
                <div className="col l-2 m-0 c-0">
                  <Category/>
                </div>
                <div className="col l-10 m-12 c-12">
                  <HomeFilter/>
                  <MobileCategory/>
                  <div className="home-product">
                        {/* <div className="row sm-gutter">                        */}
                        <Routes>
                          <Route path="/products" element={<HomeScreen/>}/>
                          <Route path="/products/:productId" element={<ProductScreen/>}/>
                       </Routes>
                        {/* </div> */}
                  </div>
                  {/* <ReactPaginate
                    previousLabel={"Previous"}
                    nextLabel={"Next"}
                    pageCount={pageCount}
                    onPageChange={changePage}
                    containerClassName={"paginationBttns"}
                    previousLinkClassName={"previousBttn"}
                    nextLinkClassName={"nextBttn"}
                    disabledClassName={"paginationDisabled"}
                    activeClassName={"paginationActive"}
      /> */}
                </div>
            </div>
          </div>
      </div>
    </div>
    </BrowserRouter>
  )
}
